import React , {useState} from "react";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "../ExpensesFilter/ExpensesFilter";
import Card from '../UI/Card';
import './Expenses.css';

function Expenses(props) {
    const [filteredYear,setFilteredYear]=useState('2020');
    const filterChangeHandler= selectedYear=>{
        setFilteredYear(selectedYear);
        console.log("Inside expenses and heres chaanged year");
        console.log(selectedYear);
    };
    return (
        <div>
        <Card className="expenses">
        <ExpensesFilter selected={filteredYear} onExpenseFilterChange={filterChangeHandler} />
            <ExpenseItem
                title={props.data[0].title}
                amnt={props.data[0].amount}
                date={props.data[0].date}
            />
            <ExpenseItem
                title={props.data[1].title}
                amnt={props.data[1].amount}
                date={props.data[1].date}
            />
            <ExpenseItem
                title={props.data[2].title}
                amnt={props.data[2].amount}
                date={props.data[2].date}
            />
            <ExpenseItem
                title={props.data[3].title}
                amnt={props.data[3].amount}
                date={props.data[3].date}
            />
        </Card>
        </div>
    );
}

export default Expenses;
