import React,{useState} from 'react';

import ExpenseForm from './ExpenseForm';
import './NewExpense.css';

const NewExpense = (props)=>{
const saveExpenseDataHandler=(enteredExpenseData)=>{
    const [isEditing,setIsEditing]=useState(false);

    const expenseData={
        ...enteredExpenseData,                                                  //const numbers1 = [1, 2, 3, 4, 5];
        id: Math.random().toString(),                                           //const numbers2 = [ ...numbers1, 1, 2, 6,7,8]; // this will be [1, 2, 3, 4, 5, 1, 2, 6, 7, 8]

    }

    const startEditingHandker=()=>{
        setIsEditing(true);
    }

    // console.log(expenseData);
    props.onAddExpense(expenseData);
};

    return (
        <div className="new-expense">
            {!isEditing && <button onClick={startEditingHandker}>Add new Expense</button>}
            {isEditing && <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} />}
        </div>
    );
};

export default NewExpense;
